				display: grid;
				grid-template-columns: 1fr 30px;
				align-items: center;
				.info {
					display: grid;
					grid-template-columns: 70px 1fr;
					grid-template-rows: 1fr 1fr;
					align-items: center;
					grid-column-gap: 14px;
					color: aliceblue;
					.song__img {
						grid-column: 1/2;
						grid-row: 1/-1;
						position: relative;
						img {
							display: block;
							width: 70px;
							height: 70px;
						}
						.icon__pause {
							position: absolute;
							top: 50%;
							left: 50%;
							width: 100%;
							height: 100%;
							line-height: 2.6;
							text-align: center;
							backdrop-filter: blur(2px);
							-webkit-backdrop-filter: blur(20px);
							transform: translate(-50%, -50%);
							font-size: 2.6rem;
						}
					}
					.song__name {
						grid-column: 2/-1;
						grid-row: 1/2;
						font-size: 1.2rem;
						margin-top: 10px;
					}
					.song__singer {
						grid-column: 2/-1;
						grid-row: 2/-1;
						margin-bottom: 10px;
						color: $text-color-week;
					}
				}
				.option {
					font-size: 1.6rem;
					color: aliceblue;
				}
			}
		}
	}

	.music-player {
		position: relative;
		&__img {
			border-radius: 10px;
			position: absolute;
			top: 0;
			left: 0;
			width: 100%;
		}
		.controls {
			position: absolute;
			top: 44%;
			left: 5%;
			width: 90%;
			height: 160px;
			padding: 20px;
			border-radius: 10px;
			background-color: rgba(#000, 0.4);
			backdrop-filter: blur(20px);
			-webkit-backdrop-filter: blur(80px);
			text-align: center;
			.song__name {
				font-size: 1.6rem;
				color: #fff;
			}
			.song__singer {
				font-size: 1.4rem;
				color: $text-color-week;
			}
			.control {
				margin-top: 10px;
				display: grid;
				grid-template-columns: repeat(3, 1fr);
				justify-content: space-evenly;
				font-size: 2rem;
				color: #fff;
				.btn-backward {
				}

				.btn-play,
				.btn-pause {
				}
				.btn-forward {
				}
			}
			.progress{
				margin-top: 24px;
				height: 3px;
				width: 100%;

			}
		}
	}
}

